mapper.select:
SELECT (
SELECT COUNT(*)
FROM sharing
WHERE member_id = #{memberId}
AND del_flag IS NULL

    AND title LIKE CONCAT('%', #{keyword}, '%')

) + (
SELECT COUNT(*)
FROM question
WHERE member_id = #{memberId}
AND del_flag IS NULL

    AND title LIKE CONCAT('%', #{keyword}, '%')

) AS total

SELECT ( SELECT COUNT(*) FROM comment c JOIN sharing s ON c.sharing_id = s.sharing_id WHERE c.writer_id = #{memberId} AND c.del_flag IS NULL
AND s.title LIKE CONCAT('%', #{keyword}, '%')
) + ( SELECT COUNT(*) FROM answer a JOIN question q ON a.question_id = q.question_id WHERE a.writer_id = #{memberId} AND a.del_flag IS NULL
AND q.title LIKE CONCAT('%', #{keyword}, '%')
) AS total

SELECT * FROM ( SELECT s.sharing_id AS id, s.member_id, m.nickname, s.title, s.created_at, 'SHARING' AS category FROM sharing s JOIN member m ON s.member_id = m.member_id WHERE s.member_id = #{memberId} AND s.del_flag IS NULL
AND s.title LIKE CONCAT('%', #{keyword}, '%')
UNION ALL
SELECT q.question_id AS id, q.member_id, m.nickname, q.title, q.created_at, 'QUESTION' AS category FROM question q JOIN member m ON q.member_id = m.member_id WHERE q.member_id = #{memberId} AND q.del_flag IS NULL
AND q.title LIKE CONCAT('%', #{keyword}, '%')
) t ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT s.sharing_id AS id, s.member_id, m.nickname, s.title, s.created_at, 'SHARING' AS category FROM sharing s JOIN member m ON s.member_id = m.member_id WHERE s.member_id = #{memberId} AND s.del_flag IS NULL
AND s.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY s.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT q.question_id AS id, q.member_id, m.nickname, q.title, q.created_at, 'QUESTION' AS category FROM question q JOIN member m ON q.member_id = m.member_id WHERE q.member_id = #{memberId} AND q.del_flag IS NULL
AND q.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY q.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT * FROM ( SELECT c.comment_id AS id, c.sharing_id AS member_id, c.writer_id, c.created_at, s.title, m.nickname, 'COMMENT' AS category FROM comment c JOIN sharing s ON c.sharing_id = s.sharing_id JOIN member m ON s.member_id = m.member_id WHERE c.writer_id = #{memberId} AND c.del_flag IS NULL
AND s.title LIKE CONCAT('%', #{keyword}, '%')
UNION ALL
SELECT a.answer_id AS id, a.question_id AS member_id, a.writer_id, a.created_at, q.title, m.nickname, 'ANSWER' AS category FROM answer a JOIN question q ON a.question_id = q.question_id JOIN member m ON q.member_id = m.member_id WHERE a.writer_id = #{memberId} AND a.del_flag IS NULL
AND q.title LIKE CONCAT('%', #{keyword}, '%')
) t ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT c.comment_id AS id, c.sharing_id AS member_id, c.writer_id, c.created_at, s.title, m.nickname, 'COMMENT' AS category FROM comment c JOIN sharing s ON c.sharing_id = s.sharing_id JOIN member m ON s.member_id = m.member_id WHERE c.writer_id = #{memberId} AND c.del_flag IS NULL
AND s.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY c.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT a.answer_id AS id, a.question_id AS member_id, a.writer_id, a.created_at, q.title, m.nickname, 'ANSWER' AS category FROM answer a JOIN question q ON a.question_id = q.question_id JOIN member m ON q.member_id = m.member_id WHERE a.writer_id = #{memberId} AND a.del_flag IS NULL
AND q.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY a.created_at DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE sharing
SET del_flag = NOW()
WHERE member_id = #{memberId}
AND sharing_id IN

    #{id}

UPDATE question
SET del_flag = NOW()
WHERE member_id = #{memberId}
AND question_id IN

    #{id}
